import React, { useState } from "react";
import { useUrls } from "./url-context";
const API_URL = "https://api.shrtco.de/v2/shorten";
const URL_REGEX = /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/;

export default function Form(props) {
  const [url, setUrl] = useState("");
  const { dispatch, state } = useUrls();
  const urls = state.urls;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isUrlValid(url)) {
      console.log("hello");

      fetchShortUrl(url);
      setUrl("");
    } else {
      alert("Please enter correct url");
    }
  };

  const copyToClipboard = (u) => {
    console.log(u);

    alert("Successfully copy URL to clipboard");
  };

  const isUrlValid = (url) => {
    return URL_REGEX.test(url);
  };

  const fetchShortUrl = (url) => {
    fetch(`${API_URL}?url=${url}`)
      .then((res) => res.json())
      .then(
        (result) => {
          dispatch({ type: "short_url", url: result.result.short_link });
          console.log(state.urls);
        },
        (error) => {
          alert(error.error);
        }
      );
  };

  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          <input
            type="text"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
          />
        </label>
        <input type="submit" value="Submit" />
      </form>

      {state.urls.length > 0 &&
        urls.map((url, index) => {
          return (
            <>
              <h3 key={index}>
                {url}
                <button onClick={() =>  navigator.clipboard.writeText(url)}>copy</button>
              </h3>
            </>
          );
        })}
    </>
  );
}
