import React, { useState } from "react";
import {useUrls} from "./url-context";
const API_URL = "https://api.shrtco.de/v2/shorten";
const URL_REGEX = /[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_\+.~#?&//=]*)?/gi;
export default function Form(props) {
  const [url, setUrl] = useState("");
  const [shortenUrl, setShortenUrl] = useState("");
  const [error, setError] = useState("");
  const {dispatch} = useCount()
  const handleSubmit = (evt) => {
    evt.preventDefault();
    const isValidUrl = URL_REGEX.test(url);
    if (isValidUrl) {
      fetch(`${API_URL}?url=${url}`)
        .then((res) => res.json())
        .then(
          (result) => {
            setShortenUrl(result.result.short_link);
            urls.dispatch()
            setError("");
          },
          (error) => {
            setShortenUrl("");
            setError(error.error);
          }

        );
    } else {
      setShortenUrl("");

      setError("Url is not valid. Please enter correct url.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
      </label>
      <input type="submit" value="Submit" />
      {error && <h3>{error}</h3>}
      <h3>{shortenUrl}</h3>
    </form>
  );
}
