import React, { useState } from "react";
import { useUrls } from "./url-context";
const API_URL = "https://api.shrtco.de/v2/shorten";
const URL_REGEX = /(http(s)?:\/\/.)?(www\.)?[-a-zA-Z0-9@:%._\+~#=]{2,256}\.[a-z]{2,6}\b([-a-zA-Z0-9@:%_\+.~#?&//=]*)/g;
export default function Form(props) {
  const [url, setUrl] = useState("");
  const { dispatch, state } = useUrls();
  const urls = state.urls;


  const handleSubmit = (evt) => {
    evt.preventDefault();


    if (isUrlValid(url)) {
      fetch(`${API_URL}?url=${url}`)
        .then((res) => res.json())
        .then(
          (result) => {

            dispatch({ type: "short_url", url: result.result.short_link });
            console.log(state.urls);
            setError("");
          },
          (error) => {

            setError(error.error);
          }
        );
    } else {


      setError("Url is not valid. Please enter correct url.");
    }
  };

  const isUrlValid = (url) => {
    return URL_REGEX.test(url);
  }

  return (
    <form onSubmit={handleSubmit}>
      <label>
        <input
          type="text"
          value={url}
          onChange={(e) => setUrl(e.target.value)}
        />
      </label>
      <input type="submit" value="Submit" />

      {state.urls.length > 0 &&
        urls.map((url, index) => {
          return <h3 key={index}>{url}</h3>;
        })}
    </form>
  );
}
